import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Footer from "../../components/footer";
import NavbarAdmin from "../../components/navbaradmin";

const EditUser = () => {
  const { id } = useParams();

  const [formData, setFormData] = useState({
    nombre: "",
    email: "",
    admin: false,
  });

  useEffect(() => {
    // Simulación: reemplaza con fetch real si tienes backend
    const cargarUsuario = async () => {
      const data = {
        nombre: "Usuario Ejemplo",
        email: "usuario@ejemplo.com",
        admin: true,
      };
      setFormData(data);
    };

    cargarUsuario();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "admin") {
      setFormData({ ...formData, admin: value === "true" });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Usuario actualizado:", { id, ...formData });
    // Aquí puedes hacer el fetch PUT/PATCH si tienes backend
  };

  return (
    <div className="d-flex flex-column min-vh-100">
      <NavbarAdmin />
      <div className="container py-5 flex-grow-1">
        <h2 className="mb-4 text-success">Editar usuario</h2>
        <form onSubmit={handleSubmit} className="bg-light p-4 rounded-4 shadow">
          <div className="mb-3">
            <label className="form-label">Nombre</label>
            <input
              type="text"
              name="nombre"
              className="form-control"
              value={formData.nombre}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Correo electrónico</label>
            <input
              type="email"
              name="email"
              className="form-control"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Rol</label>
            <select
              name="admin"
              className="form-select"
              value={formData.admin.toString()}
              onChange={handleChange}
              required
            >
              <option value="false">Usuario</option>
              <option value="true">Administrador</option>
            </select>
          </div>

          <button type="submit" className="btn btn-success">Guardar cambios</button>
        </form>
      </div>
      <Footer />
    </div>
  );
};

export default EditUser;
