import { useNavigate } from "react-router-dom";
import NavbarAdmin from "../../components/navbaradmin";
import Footer from "../../components/footer";

const AdminUsers = () => {
  const navigate = useNavigate();

  const usuarios = [
    { id: 1, nombre: "Mar칤a L칩pez", email: "maria@example.com", rol: "Usuario" },
    { id: 2, nombre: "Pedro Ruiz", email: "pedro@example.com", rol: "Administrador" },
  ];

  const handleEdit = (id) => {
    navigate(`/admin/editar-usuario/${id}`);
  };

  return (
    <div className="d-flex flex-column min-vh-100">
      <NavbarAdmin />
      <div className="container py-5 flex-grow-1">
        <h2 className="mb-4 text-success">游논 Gesti칩n de Usuarios</h2>
        <table className="table table-hover">
          <thead className="table-light">
            <tr>
              <th>Nombre</th>
              <th>Email</th>
              <th>Rol</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {usuarios.map((usuario) => (
              <tr key={usuario.id}>
                <td>{usuario.nombre}</td>
                <td>{usuario.email}</td>
                <td>{usuario.rol}</td>
                <td>
                  <button
                    className="btn btn-sm btn-outline-success me-2"
                    onClick={() => handleEdit(usuario.id)}
                  >
                    Editar
                  </button>
                  <button className="btn btn-sm btn-outline-danger">Eliminar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <Footer />
    </div>
  );
};

export default AdminUsers;
