// src/pages/CreateEvent.jsx

import { useState } from 'react';

const CreateEvent = () => {
  const [formData, setFormData] = useState({
    titulo: '',
    descripcion: '',
    fecha: '',
    hora: '',
    lugar: '',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Evento enviado:', formData);
    // Aquí puedes hacer un fetch o axios.post al backend
  };

  return (
    <div className="container py-5">
      <h2 className="mb-4">✨ Crear Nuevo Evento</h2>

      <form onSubmit={handleSubmit} className="bg-light p-4 rounded-4 shadow">
        <div className="mb-3">
          <label className="form-label">Título del evento</label>
          <input
            type="text"
            name="titulo"
            className="form-control"
            value={formData.titulo}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Descripción</label>
          <textarea
            name="descripcion"
            className="form-control"
            value={formData.descripcion}
            onChange={handleChange}
            rows="3"
            required
          ></textarea>
        </div>

        <div className="row">
          <div className="col-md-4 mb-3">
            <label className="form-label">Fecha</label>
            <input
              type="date"
              name="fecha"
              className="form-control"
              value={formData.fecha}
              onChange={handleChange}
              required
            />
          </div>

          <div className="col-md-4 mb-3">
            <label className="form-label">Hora</label>
            <input
              type="time"
              name="hora"
              className="form-control"
              value={formData.hora}
              onChange={handleChange}
              required
            />
          </div>

          <div className="col-md-4 mb-3">
            <label className="form-label">Lugar</label>
            <input
              type="text"
              name="lugar"
              className="form-control"
              value={formData.lugar}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        <button type="submit" className="btn btn-primary mt-3">Guardar evento</button>
      </form>
    </div>
  );
};

export default CreateEvent;
