import { useState } from 'react';
import { Link } from 'react-router-dom';

const Login = () => {
  const [form, setForm] = useState({
    correo: '',
    contraseña: ''
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Inicio de sesión:', form);
  };

  return (
    <div
      className="d-flex justify-content-center align-items-center"
      style={{
        minHeight: '100vh',
        backgroundColor: '#DAFDDC',
        padding: '1rem'
      }}
    >
      <div
        className="card shadow-lg p-4 rounded-4 border border-4 border-success border-opacity-75"
        style={{
          width: '100%',
          maxWidth: '500px',
          backgroundColor: '#93E495',
          boxShadow: '0 0 24px 4px #25a36699'
        }}
      >
        {/* ✅ Títulos estilizados */}
        <h1
          className="text-center mb-2"
          style={{
            color: '#065f46',
            fontSize: '2.5rem',
            fontWeight: 'bold'
          }}
        >
          ¡Bienvenido!
        </h1>
        <h4
          className="text-center mb-4"
          style={{
            color: '#1b4332',
            fontWeight: '500'
          }}
        >
          Inicia sesión con tu cuenta
        </h4>

        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Correo electrónico</label>
            <input
              type="email"
              className="form-control"
              name="correo"
              value={form.correo}
              onChange={handleChange}
              placeholder="example@gmail.com"
              required
            />
          </div>
          <div className="mb-4">
            <label className="form-label">Contraseña</label>
            <input
              type="password"
              className="form-control"
              name="contraseña"
              value={form.contraseña}
              onChange={handleChange}
              placeholder="••••••"
              required
            />
          </div>
          <Link type="submit" to="/home" className="btn btn-success w-100">Entrar</Link>
        </form>
      </div>
    </div>
  );
};

export default Login;
