import Navbar from '../../components/navbar';
import Footer from '../../components/footer';

const Perfil = () => {
  // Datos de ejemplo, reemplazar por datos reales del usuario
  const usuario = {
    nombre: 'Juan P√©rez',
    correo: 'juan.perez@email.com',
    rol: 'Usuario',
    fechaRegistro: '10 de enero de 2024',
  };

  return (
    <>
      <Navbar />
      <div className="container py-5">
        <div className="row justify-content-center">
          <div className="col-12 col-md-8 col-lg-6">
            <div className="card shadow-lg border-success border-3 rounded-4 bg-white bg-opacity-75">
              <div className="card-body text-center">
                <span style={{ fontSize: '4rem' }}>üë§</span>
                <h2 className="text-success fw-bold mt-3 mb-1">Mi perfil</h2>
                <p className="text-success-emphasis mb-4">Informaci√≥n personal del usuario</p>
                <ul className="list-group list-group-flush mb-4">
                  <li className="list-group-item bg-transparent"><span className="fw-bold">üßë Nombre:</span> {usuario.nombre}</li>
                  <li className="list-group-item bg-transparent"><span className="fw-bold">üìß Correo:</span> {usuario.correo}</li>
                  <li className="list-group-item bg-transparent"><span className="fw-bold">üîë Rol:</span> {usuario.rol}</li>
                  <li className="list-group-item bg-transparent"><span className="fw-bold">üìÖ Registro:</span> {usuario.fechaRegistro}</li>
                </ul>
                <button className="btn btn-success px-4">‚úèÔ∏è Editar perfil</button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Perfil;
